import React from "react";

const SolutionComponent = () => {
  const styles = {
    container: {
      position: "relative",
    },
    titleText: {
      fontWeight: "700",
      fontSize: "64px",
      textAlign: "center",
    },
  };

  const list = [
    { imgPath: "/assets/images/solution1.png", title: "3D Art", list: [] },
    {
      imgPath: "/assets/images/solution2.png",
      title: "3D Art",
      list: ["For presentation", "For media", "For game"],
    },
    {
      imgPath: "/assets/images/solution3.png",
      title: "3D Art",
      list: ["NFT", "Online", "Offline", "Website"],
    },
  ];

  const displayContent = (
    <div style={styles.container}>
      <div className="d-flex flex-nowrap">
        {list?.map((item) => {
          return (
            <div>
              <img src={item.imgPath} alt={item.title} />
              <div>
                <div>{item.title}</div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );

  return (
    <div>
      <div style={styles.titleText}>SOLUTION</div>
      {displayContent}
    </div>
  );
};

export default SolutionComponent;
