import React from "react";

const ProjectCard = ({ title, date, concept, imgPath }) => {
  const styles = {
    cardContainer: {
      //   fontSize: "50px",
    },
    cardTitle: {
      fontSize: "72px",
      fontWeight: "800",
      textTransform: "capitalize",
    },
    cardType: {
      fontSize: "50px",
      fontWeight: "800",
      textTransform: "capitalize",
    },
    cardDate: {
      fontSize: "31px",
      fontWeight: "400",
      textTransform: "capitalize",
    },
    cardConcept: {
      fontSize: "24px",
      fontWeight: "300",
    },
    img: {
      position: "absolute",
      width: "564px",
      height: "512px",
      left: "calc(50% - 564px/2 + 25px)",
      top: "calc(50% - 512px/2 - 61.5px)",
    },
  };

  const displayCard = (
    <div className="my-3 container" style={styles.cardContainer}>
      <div className="row ">
        <div className="col-md-4">
          <div className="position-relative">
            <img
              src={imgPath}
              // className="img-fluid rounded-start"
              style={styles.img}
              alt={title}
            />
          </div>
        </div>
        <div className="col-md-8">
          <div className="card-body">
            <div style={styles.cardType}>PROJECT</div>
            <div style={styles.cardTitle}>{title}</div>
            <div style={styles.cardDate}>YEAR : {date}</div>
            <div style={styles.cardConcept}>
              <span style={{ fontWeight: "500" }}>CONCEPT</span> : {concept}
            </div>
          </div>
        </div>
      </div>
    </div>
  );

  return <div>{displayCard}</div>;
};

export default ProjectCard;
